import { Injectable } from '@angular/core';
import axios from 'axios';

export interface Staff {
  staffId: string;
  fullName: string;
  birthday: string;
  gender: number;
}

@Injectable({ providedIn: 'root' })
export class StaffService {
  private baseUrl = 'https://localhost:5001/api/staff'; // your ASP.NET backend URL

  async getAll(): Promise<Staff[]> {
    const res = await axios.get(this.baseUrl);
    return res.data;
  }

  async getById(id: string): Promise<Staff> {
    const res = await axios.get(`${this.baseUrl}/${id}`);
    return res.data;
  }

  async add(staff: Staff) {
    await axios.post(this.baseUrl, staff);
  }

  async update(id: string, staff: Staff) {
    await axios.put(`${this.baseUrl}/${id}`, staff);
  }

  async delete(id: string) {
    await axios.delete(`${this.baseUrl}/${id}`);
  }

  async search(criteria: any): Promise<Staff[]> {
    const params = new URLSearchParams(criteria);
    const res = await axios.get(`${this.baseUrl}/search?${params.toString()}`);
    return res.data;
  }
}
